		)

select count(*) from (select a.*, b.userName from (select board.*

) a, mv_user
b where
a.userNo=b.userNo

) where boardCategory=#{boardCategory}

select * from (select rownum rm, a.*, b.userName from (select * from (select board.*
) where boardCategory=#{boardCategory} order by boardNo desc) a, mv_user b where a.userNo=b.userNo
)where rm between (#{pageStart}+1) and (#{pageStart}+#{perPageNum})

